// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  const now = new Date();
  const tomorrow = new Date(now.getTime() + 24 * 60 * 60 * 1000); // Add 1 day in ms
  item.json.timestamp = tomorrow.toISOString(); // Compatible with Supabase timestampz
}

return $input.all();
